mapper.select:
SELECT ch.title AS title, ch.id AS chapterId, CASE WHEN ch.price IS NOT NULL AND ch.price > 0 THEN TRUE ELSE FALSE END AS feeable, CASE WHEN ca.id IS NOT NULL THEN TRUE ELSE FALSE END AS paied, ch.chapter_idx AS idx FROM novel_chapter ch LEFT JOIN chapter_access ca ON ca.chapter_id = ch.id AND ca.user_id = #{userId}
ch.book_id = #{novelId} AND (ch.is_delete = 0 OR ch.is_delete IS NULL)
ORDER BY ch.chapter_idx ASC
